    public $tokenAddress= '0x471B017DbfA2515E25852914463b6dc054c5fEA1';
    public $tokenDec= 6;
    public $usdtAddress= '0x1A480D4847BC878CF2f0DfeA6E9C1297D0fdA512';
    public $routeAddress= '0x639B70bA02cF19E999a5eF770b2BF91B05Ed9804';
    public $rpcUrl= 'https://data-seed-prebsc-1-s1.binance.org:8545/';
    public $web3;
    public $token;
    public $usdt;
    public $route;
    public $wallet;


    function __construct()
    {
        $this->web3 = new Web3($this->rpcUrl);
        $this->token = new Contract($this->web3->provider, $this->tokenabi);
        $this->token->at($this->tokenAddress);
        $this->usdt = new Contract($this->web3->provider, $this->usdtabi);
        $this->usdt->at($this->usdtAddress);
        $this->route = new Contract($this->web3->provider, $this->routeabi);
        $this->route->at($this->routeAddress);
    }

    public function approve($to,$amount){
        $data=$this->usdt->getData('approve',$to,bcmul($amount,10**18));
        return $this->tx($this->usdt->getToAddress(),'0x'.$data);
    }

    public function swap($amount){
        $amountBig=bcmul($amount,10**$this->tokenDec);

        $data=$this->route->getData('swapTokensForExactTokens',$amountBig,$this->getamount($amountBig),[$this->usdtAddress,$this->tokenAddress],$this->wallet->getAddress(),time()+60);
        return $this->tx($this->route->getToAddress(),'0x'.$data);

    }

    public function getamount($amount){
        $data=$this->route->getData('getAmountsIn',$amount,[$this->usdtAddress,$this->tokenAddress]);

        $txdata=[
            'from' => $this->wallet->getAddress(),
            'to' => $this->route->getToAddress(),
            'value' => '0x0',
            'data' => '0x'.$data,
        ];
        $hex=$this->jsonrpc('eth_call',[$txdata,'latest']);
        $hex = substr($hex,130,64);
        return hexdec($hex);

    }


    public function tx($to,$data){

        $chainId=$this->jsonrpc('net_version',[]);
        $gasPrice=$this->jsonrpc('eth_gasPrice',[]);
        $nonce=$this->jsonrpc('eth_getTransactionCount',[$this->wallet->getAddress(),'latest']);
        $gas=$this->jsonrpc('eth_estimateGas',[['from'=>$this->wallet->getAddress(),'to'=>$to,'data'=>$data]]);

        $transaction = new Transaction([
            'nonce' => $nonce,
            'from' => $this->wallet->getAddress(),
            'to' => $to,
            'gas' => $gas,
            'gasPrice' => $gasPrice,
            'value' => '0x0',
            'chainId' => $chainId,
            'data' => $data
        ]);


        $signedTransaction = $transaction->sign($this->wallet->getPrivateKey());

        return $this->jsonrpc('eth_sendRawTransaction',['0x'.$signedTransaction]);
    }

    public function setKey($key){
        $this->wallet=Wallet::createByPrivate($key);
    }

    public function jsonrpc($method,$param){
        $client = new Client($this->rpcUrl);
        $client->call($method, $param);
        if(!empty($client->error)){
            throw new \Exception($client->error);
        }
        return $client->result;
    }

}



